  const insertManyComments = db.transaction(() => {
    let commentId = 1;

    for (let postId = 1; postId <= 100; postId++) {
      // 2-4 root comments per post
      const numRootComments = 2 + Math.floor(Math.random() * 3);

      for (let i = 0; i < numRootComments; i++) {
        const userId = 1 + Math.floor(Math.random() * 100);
        const body = commentTexts[Math.floor(Math.random() * commentTexts.length)];
        const score = Math.floor(Math.random() * 50) - 5;
        insertComment.run(postId, userId, null, body, score);
        const rootId = commentId++;

        // 0-3 replies per root comment
        const numReplies = Math.floor(Math.random() * 4);
        let parentId = rootId;

        for (let j = 0; j < numReplies; j++) {
          const replyUserId = 1 + Math.floor(Math.random() * 100);
          const replyBody = replyTexts[Math.floor(Math.random() * replyTexts.length)];
          const replyScore = Math.floor(Math.random() * 30) - 3;
          insertComment.run(postId, replyUserId, parentId, replyBody, replyScore);
          // Sometimes reply to the reply (threading)
          if (Math.random() > 0.5) {
            parentId = commentId;
          }
          commentId++;
        }
      }
    }
  });
